import React, { useState, useRef } from 'react'
import { v4 as uuidv4 } from 'uuid';

export default function Search(props) {

    const {
        gameString
    } = props;  


    // const [foundGames, setGames] = useState([])
    const gameNameRef = useRef()

    function handleGameSearch(e) {

        const query = gameNameRef.current.value

        if (query === '') return

        setGames(prevGames => {
            return [...prevGames, { id: uuidv4(), }]
        })
    }

    return (
        <div className="page-search">
            <form action="">
                <input ref={gameNameRef} id="game-filter" onChange={handleGameSearch} type="text" className="name-search" placeholder="Search" />
                <button type="submit"><span className="icon-search"></span></button>
            </form>
        </div>
    )
}
