import React, { useRef } from 'react'

export default function Search(props) {

    const {
        onGameSearch,
        gameString,
        gameNameRef
    } = props;  


    function handleGameSearch(e) {

        const gameName = gameNameRef.current.value

        if (gameName === '') return

        setGames(prevGames => {
            return [...prevGames, { id: uuidv4(), }]
        })
    }

    return (
        <div className="page-search">
            <form action="">
                <input ref={gameNameRef} id="game-filter" onChange={handleGameSearch} value={gameString} type="text" className="name-search" placeholder="Search" />
                <button type="submit"><span className="icon-search"></span></button>
            </form>
        </div>
    )
}
