                    <h1>Slots</h1>
                </div>
                <Search
                    ref={gameNameRef}
                    onGameSearch={handleGameSearch}
                    gameString={gameString}/>
                <div className="page-filters">
                    <div className="filter active" data-filter="all"><span className="icon-all"></span><p>All</p></div>
                    <div className="filter" data-filter="new"><span className="icon-new"></span><p>New</p></div>
                    <div className="filter" data-filter="top"><span className="icon-top"></span><p>Top</p></div>
                </div>
            </div>
            <div className="page-lower">
                <GameList />
            </div>
        </div>
    );
}

export default App;
