import React from 'react'
import parse from 'html-react-parser';

export default function GameItem({ game }) {

    const newLabel = parse('<span className="game-label new"><img src="./assets/images/labels/new.png" alt="New label"></span>');
    // const highLimit = "<span className=\"game-label high-limit\"><img src=\"assets/images/labels/high-limit.png\" alt=\"High limit label\"></span>";
    const labels = [];

    const newGame = (label) => label === "new";

    if (game.labels.includes("new")) labels.push(newLabel);

    return (
        <div className={`box ${game.size}`} data-game={game.name} data-tags={game.tags}>
            <a href={game.url}>
                <img className="game-img" src={require(`${game.image}`)} alt={game.name} />
                {labels}
            </a>
        </div>
    )
}
